themes/riverside/public/_includes/pagina.content:
{% if page_number == 0 %}
{% else %}
    <br/>
    <ul class="page_numbers pull-right">
        {% if page_number %}
        <li class="page_info"><p>Page {{ current_page }} of {{ page_number + 1}}</p></li>
            {% for page_number in range(1, page_number + 2) %}
                {% if page_number == current_page %}
                    <li class="active"><a>{{ page_number }}<span class="sr-only">(current)</span></a></li>
                {% elif page_number == 1 %}
                <li><a href="{{ ROOT_PATH }}/">{{ page_number }}</a></li>
                {% else %}
                <li><a href="{{ ROOT_PATH }}/blog/page/{{ page_number }}">{{ page_number }}</a></li>
                {% endif %}
            {% endfor %}
        {% endif %}
    </ul>
{% endif %}

themes/riverside/public/archives/index.h.content:
---
layout: default
title: Archives
---

<div class="container innerpage_content">
  <div class="row">

    <div class="col-lg-9 col-md-9 col-sm-9 col-xs-9 box">
      {% set this_year = 0 %}

        <h1>Archives</h1>
        <hr/>
        {% for post in posts %}
        {% set this_month = post.pub_time.strftime('%b') %}
        {% if this_year != post.pub_time.strftime('%Y') %}
        {% if this_year != 0 %}
        {% endif %}
        {% set this_year = post.pub_time.strftime('%Y') %}
        <h2 id="{{this_year}}" class="blog-archives">{{ this_year }}</h2>
        {% else %}
        {% endif %}
        <div class="archives">
          <h3 class="archives-title"><a href="{{ ROOT_PATH }}{{ post.url }}">{{ post.title }}</a></h3>
          <p class="archives-time">{{ post.pub_time.strftime('%b %d') }}</p>
          <div class="archives-footer"><span>Posted in {% for category in post.categories %}<a href="{{ ROOT_PATH }}/categories/#{{ category | title }}">{{ category | title}}</a> {% endfor %}</span></div>
        </div>
        {% endfor %}
      </div>

    {% include "_includes/sidebar.html" %}
  </div>
</div>

